mapper.select:
 SELECT tc.*,u.id as userid, u.phone as u_phone,u.username,u.real_name,u.nickname,u.email,u.honer_score,u.grade_total,u.grade_times
FROM happy_help.hh_task_comment tc left join happy_help.hh_user u on tc.user_id = u.id
WHERE tc.hh_task_info_id = #{taskInfoId,jdbcType=INTEGER}

mapper.insert:
insert into hh_task_comment (id, context, hh_task_info_id, 
  user_id, user_nickname, create_time
  )
values (#{id,jdbcType=INTEGER}, #{context,jdbcType=VARCHAR}, #{hhTaskInfoId,jdbcType=INTEGER}, 
  #{userId,jdbcType=INTEGER}, #{userNickname,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )
